import { MDBContainer, MDBRow, MDBCol, MDBCardBody, MDBInput, MDBBtn, MDBModalFooter } from 'mdbreact';
import React, { Component } from 'react';
import SocialButtonList from '../SocialButtonList/SocialButtonList';
import { auth } from '../../firebase'
import './Login.css'
import { Link } from 'react-router-dom';
import AnimatedPage from '../../animations/AnimatedPage'
import Blur from 'react-css-blur'

const buttonList = {
  github: {
    visible: true,
    provider: () => {
      const provider = auth.githubOAuth();
      provider.addScope('user');
      return provider;
    }
  },
  google: {
    visible: true,
    provider: () => auth.googleOAuth()
  },
  facebook: {
    visible: true,
    provider: () => auth.facebookOAuth()
  }
};

class Login extends Component {
  componentDidMount() {
    auth.getAuth().onAuthStateChanged(user => {
      if (user) {
        this.props.history.push('/home');
      }
    });
  }

  render() {
    return (
      <AnimatedPage>
        <MDBContainer>
          <MDBRow style={{ justifyContent: 'flex-start' }}>
            <Blur radius='4px'>
            <MDBCol md="6" style={{ borderRadius: 20, filter: 'blur 5px', boxShadow: "-2px -2px 12px #9E9E9E" }}>

            </MDBCol>
            </Blur>
          </MDBRow>

          <MDBRow style={{ marginTop: "15%" }}>
            <MDBCol md="6">
              <MDBCardBody className="mx-4">
              </MDBCardBody>
            </MDBCol>
          </MDBRow>
        </MDBContainer>
      </AnimatedPage>
    );
  }
}

export default Login;
