import React from 'react';
import firebase from 'firebase'
import StyledFirebaseAuth from 'react-firebaseui/StyledFirebaseAuth';

firebase.initializeApp({
    apiKey: 'AIzaSyCdBEhL6U7nkbmYvL-jOwits4aj378KFx4',
    authDomain: 'portfolyo-2c969.firebaseapp.com',
    projectId: 'portfolyo-2c969',
})

export default class Login extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            isLoggedIn: false
        }
        this.firebaseUiConfiguration = {
            signInFlow: 'popup',
            signInOptions: [
                firebase.auth.GoogleAuthProvider.PROVIDER_ID,
                firebase.auth.FacebookAuthProvider.PROVIDER_ID,
                firebase.auth.GithubAuthProvider.PROVIDER_ID,
                firebase.auth.EmailAuthProvider.PROVIDER_ID,
            ],
            signInSuccessUrl: '/home',
        }
    }

    componentDidMount = () => {
        firebase.auth().onAuthStateChanged(user => {
            this.setState({ isLoggedIn: user !== null })
        })
    }

    render() {
       // if (this.state.redirect)
           // return (<Redirect to={'/home'} />);
        /*else*/ {
            return (
                <StyledFirebaseAuth 
                    uiConfig={this.firebaseUiConfiguration}
                    firebaseAuth={firebase.auth()}
                />
            );
        }
    }
}
