import React from 'react';
import { withRouter, Route, Switch } from 'react-router-dom';
import Login from '../components/Login/Login';
import Dashboard from '../components/Dashboard/Dashboard';
import About from '../components/About';
import withAuthentication from '../containers/withAuthentication';
import { CSSTransition, TransitionGroup } from 'react-transition-group';
import Register from '../components/Register/Register'
import Error404 from '../components/Error404'
import './App.css';
import '../animations/pageSlide.scss'

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      prevDepth: this.getDepth(this.props.location)
    }
  }

  getDepth(location) {
    let pathArr = location.pathname.split('/');
    pathArr = pathArr.filter(c => c !== '');
    return pathArr.length;
  }

  componentWillReceiveProps() {
    this.setState({ prevDepth: this.getDepth(this.props.location) })
  }

  render() {
    const { location } = this.props;
    return (
      <div className='app'>

        <TransitionGroup component='div'>
          <CSSTransition
            mountOnEnter={false}
            unmountOnExit={true}
            key={location.pathname.split("/")[1] || '/'}
            classNames="pageSlide"
            timeout={{
              enter: 800,
              exit: 800
            }}
          >
            <div className={this.getDepth(location) - this.state.prevDepth >= 50 ? 'left' : 'right'}>
              <Switch location={location}>
                <Route exact path="/" component={Login} />
                <Route path='/register' component={Register} />
              </Switch>
            </div>
          </CSSTransition>
        </TransitionGroup >

        <Switch>
          <Route path="/about" component={About} />
          <Route path='/home' component={withAuthentication(Dashboard)} />
          {/* <Route component={Error404} /> */}
        </Switch >
      </div >
    );
  }
}
export default withRouter(App);
